def Reload_Weapon():
    global Pistolfired
    for index in range(3):
        pause(1000)
        Pistolfired = False
    statusbar.value = statusbar.max

def on_life_zero():
    game.over(False, effects.dissolve)
info.on_life_zero(on_life_zero)

def on_on_zero2(status):
    Reload_Weapon()
statusbars.on_zero(StatusBarKind.Ammunition, on_on_zero2)

def cleanEnemys():
    global Enemys
    Enemys = sprites.all_of_kind(SpriteKind.enemy)
    for value in Enemys:
        value.destroy(effects.fire, 500)
